	def fnUpdateLog(String sLogContent)
	{
		def logFileLocn = context.expand( '${#Project#ProjectLog}' )	
		def logFile = new File(logFileLocn)
		logFile.append(new Date().format('yyyyMMddHHmmss')+ " : "+sLogContent +"\n")
	}
} //End of class

