						log.info "it.ItemComment.DisplayOrder.toString(>>>>"+it.ItemComment.DisplayOrder[0].toString()
						log.info "it.Required.toString()>>>"+it.Required.toString()
						log.info "it.PictureRequired.toString()>>>"+it.PictureRequired.toString()
						log.info "it.NonBugdetItem.toString()>>>"+it.NonBugdetItem.toString()
						log.info "it.PriceOverride.toString()>>>"+it.PriceOverride.toString()
						log.info "it.ObjCClassName.toString()>>>"+it.ObjCClassName.toString()
						log.info "it.DefaultMaterial.toString()>>>"+it.DefaultMaterial.toString()
						log.info "it.DefaultLabor.toString()>>>"+it.DefaultLabor.toString()
						log.info "it.DefaultHours.toString()>>>"+it.DefaultHours.toString()
						log.info "it.GLCode.toString()>>>"+it.GLCode.toString()
						log.info "it.Category.toString()>>>"+it.Category.toString()
						log.info "it.SubCategory.toString()>>>"+it.SubCategory.toString()*/
						//it.ItemComment.Comment[0].toString() == sComment && it.ItemComment.DisplayOrder[0].toString() == sDisplayOrder  && it.Required.toString() == sRequired &
						if ( it.PictureRequired.toString() == sPictureRequired  && it.NonBugdetItem.toString() == sNonBugdetItem && it.PriceOverride.toString() == sPriceOverride  && it.ObjCClassName.toString() == sObjCClassName && it.DefaultMaterial.toInteger().toString() == sDefaultMaterial && it.DefaultLabor.toInteger().toString() == sDefaultLabor && it.DefaultHours.toInteger().toString() == sDefaultHours && it.GLCode.toString() == sGLCode && it.Category.toString() == sCategory && it.SubCategory.toString() == sSubCategory)
						{log.info "In second true"
							bFlag = true
							//log.info "Actual Item Id : "+it.PWItemID.toString() +" matches with Expected Item Id : "+iNewItemId.toString()
							//fnUpdateLog("Actual Item Id : "+it.PWItemID.toString() +" matches with Expected Item Id : "+iNewItemId.toString() )
							fnGenerateTCStepsReport("Verify Item Name in View Item Response", "Actual Item name should match with Expected Item name ", "Actual Item Name : "+it.Name.toString() +" matches with Expected Item Name : "+sNewItemName+" for Office ID : "+it.AppOfficeID.toString()+" and Office Name : "+sOfficeName+" (Corporation : "+sCorporationName+")", "Pass")
							log.info "Actual Item Name : "+it.Name.toString() +" matches with Expected Item Name : "+sNewItemName+" for Office ID : "+it.AppOfficeID.toString()+" and Office Name : "+sOfficeName
							fnUpdateLog("Actual Item Name : "+it.Name.toString() +" matches with Expected Item Name : "+sNewItemName+" for Office ID : "+it.AppOfficeID.toString()+" and Office Name : "+sOfficeName)
							return true
						}
					}	
				}

				if(bFlag == true)
				{
					fnGenerateTCStepsReport("Validate Newly Added Item ", "Newly added Item should be validated", "Newly added Item validated successfully", "Pass")
					log.info "Newly added Item validated successfully"
					fnUpdateLog("Newly added Item validated successfully")
				}
				else
				{
					fnGenerateTCStepsReport("Validate Newly Added Item ", "Newly added Item should be validated", "Newly added Item NOT validated. Item NOT found or Item data NOT matching.", "Fail")
					log.info "Newly added Item NOT found or Item data NOT matching"
					fnUpdateLog("Newly added Item NOT found or Item data NOT matching")
					testRunner.fail("Newly added Item NOT found or Item data NOT matching")
				}
			}
			else
			{
			fnGenerateTCStepsReport("Verify View Item Response", "View Item response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please try again with valid service end-point..", "Fail")
			fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please try again..\n");
			testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please try again..\n")
			}

			//log.info "Status: "+ testRunner.getStatus()  //Handeled in teardown script

		}
		else
		{
			fnGenerateTCStepsReport("Verify Add Item Response", "Add Item response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request. Please try again with valid service end-point..", "Fail")
			fnUpdateLog("Response is Null for ["+ sTCName+ "] request. Please try again with valid service end-point..");
			testRunner.fail("Response is Null for ["+ sTCName+ "] request. Please try again with valid service end-point..")
		}
	}
	catch (Exception e)
	{
		fnGenerateTCStepsReport("Error Handler ", "There should NOT be any error/exception in the test", "Exception found in current test.", "Fail")
		testRunner.fail("[Add Item] failed with exception : "+e)
		fnUpdateLog("[Add Item] failed with exception : "+e)
	}
	finally
	{
		log.info "[Add Item] Execution Completed Successfully......."
		fnUpdateLog("[Add Item] Execution Completed Successfully.......\n")
	}
