	def int fnRandomNum(min, max) 
	{
		try
		{
		   Random rand = new Random();
		   int randomNum = rand.nextInt((max - min) + 1) + min;
		   return randomNum;
		}
		catch (Exception e)
		{
			testRunner.fail("Function [fnRandomNum] failed with exception : "+e)
			testRunner.fail("Function [fnRandomNum] failed with exception : "+e)
		}
	}



	def fnRunRequest(String sReqName)
	{
		try
