			testRunner.fail("Function [fnRunRequest] failed with exception : "+e)
		}
	}

	def fnValidateParamValues(String sParamName, String sParamAct, String sParamExp)
	{
		try
		{
			if(sParamAct == sParamExp)
			{
				log.info "Expected Value : "+sParamExp +" matches with Actual Value : "+sParamAct +" for Parameter : "+sParamName
				fnUpdateLog("Expected Value : "+sParamExp +" matches with Actual Value : "+sParamAct +" for Parameter : "+sParamName)
			}
			else
			{
				log.info "Expected Value : "+sParamExp +" NOT matching with Actual Value : "+sParamAct +" for Parameter : "+sParamName
				fnUpdateLog("Expected Value : "+sParamExp +" NOT matching with Actual Value : "+sParamAct +" for Parameter : "+sParamName)
			}
		}
		catch (Exception e)
		{
			testRunner.fail("Function [fnValidateParamValues] failed with exception : "+e)
		}
	}
