				fnUpdateLog("Total Items After Duplicate Add Attempt : "+iItemListSize.toString())	

				int iDupItemCount = 0 

				jsonResponse["PWItemItems"].find
				{ 
					//log.info it
					//if(it.AppItemID == 367 && it.Name == "TestItem_367" )  //Just for testing
					//if(it.AppItemID == iNewItemId && it.Name == sNewItemName )  //No need to validate on Item Id, It's auto generated by DB
					if(it.Name == sNewItemName )
					{	
						iDupItemCount = iDupItemCount +1
						bFlag = false
						//log.info "Actual Item Id : "+it.AppItemID.toString() +" matches with Expected Item Id : "+iNewItemId.toString()+" It should NOT be added."
						//fnUpdateLog("Actual Item Id : "+it.AppItemID.toString() +" matches with Expected Item Id : "+iNewItemId.toString()+" It should NOT be added.")
						if(iDupItemCount>=2)
						{	
							fnGenerateTCStepsReport("Verify Item Name in View Item Response", "Item should NOT be added ", "Actual Item Name : "+it.Name.toString() +" matches with Expected Item Name : "+sNewItemName+". It should NOT be added.", "Fail")
							log.info "Actual Item Name : "+it.Name.toString() +" matches with Expected Item Name : "+sNewItemName+" It should NOT be added."
							fnUpdateLog("Actual Item Name : "+it.Name.toString() +" matches with Expected Item Name : "+sNewItemName+" It should NOT be added.")
						}
						//return true  //Look for two duplocate Items
					}	
				}

				log.info "iDupItemCount>>>>>>>>>>>>>>>"+iDupItemCount
				if(bFlag == false && iDupItemCount>=2)
				{
					fnGenerateTCStepsReport("Verify Duplicate Item Name in View Item Response", "Duplicate Item should NOT be added ", "Two duplicate Items found.", "Fail")
					log.info "Duplicate Item added and found. Test failed.."
					fnUpdateLog("Duplicate Item added and found. Test failed..")
					testRunner.fail("Duplicate Item added and found. Test failed..")
				}
				else
				{
					fnGenerateTCStepsReport("Verify Duplicate Item Name in View Item Response", "Duplicate Item should NOT be added ", "Duplicate Item NOT found successfully", "Pass")
					log.info "Duplicate Item NOT found successfully"
					fnUpdateLog("Duplicate Item NOT found successfully")
				}
			}
			else
			{
				fnGenerateTCStepsReport("Verify View Item Response", "View Item response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please try again with valid service end-point..", "Fail")
				fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request or . Please try again..\n");
				testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please try again..\n")
			}

		//log.info "[Duplicate Item] Execution Ended......."
		//fnUpdateLog("[Duplicate Item] Execution Ended.......\n")
		//log.info "Status: "+ testRunner.getStatus()  //Handeled in teardown script
		}
		else
		{
			fnGenerateTCStepsReport("Verify Duplicate Add Item Response", "Duplicate Add Item response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Duplicate Item is added. Please try again with valid service end-point..", "Fail")
