		if (responseContent!="")
		{
			def reqF1 = new File(sJSONPath+"\\Request_"+sTCName)
			def resF1 = new File(sJSONPath+"\\Response_"+sTCName)
			if (!reqF1.exists()){reqF1.mkdirs()}
			if (!resF1.exists()){resF1.mkdirs()}

			def reqFile1 = new File(sJSONPath+"\\Request_"+sTCName+"\\Request_"+testRunner.testCase.getTestStepAt(1).getName()+".json")
			reqFile1.write(requestContent)
			def respFile1 = new File(sJSONPath+"\\Response_"+sTCName+"\\Response_"+testRunner.testCase.getTestStepAt(1).getName()+".json")
			respFile1.write(responseContent)

			fnUpdateLog("Service Request/Response Captured Successfully for Test Case ["+sTCName+"]");

			jsonResponse = new JsonSlurper().parseText(responseContent)
			String sCode = jsonResponse.code
			String sStatus = jsonResponse.status
			String sErrors = jsonResponse.errors
			log.info sCode+sStatus+sErrors
			//if(sKey=="Status" && sValue=="Add Item Failed")
			if(sCode=="RQ500" && sStatus=="500")
			{
				fnGenerateTCStepsReport("Verify Max Length Item Response", "Max Length Item response should throw errors", "Max Length Item NOT added and response has errors", "Pass")
				log.info "Max Length Item : "+sNewItemName+" NOT added successfully"
				fnUpdateLog("Max Length Item : "+sNewItemName+" NOT added successfully")	
			}
			else
			{
				fnGenerateTCStepsReport("Verify Max Length Item Response", "Max Length Item response should throw errors", "Max Length Item added and response has NO errors", "Fail")
				log.info "Max Length Item : "+sNewItemName+" should NOT be added"
				fnUpdateLog("Max Length Item : "+sNewItemName+" should NOT be added")	
				testRunner.fail("Max Length Item : "+sNewItemName+" should NOT be added")
			}
			//End - Adding a new Item

			//Start - Verify newly added Item
			boolean bFlag = true
			fnRunRequest("ViewItems");
			requestContent = context.expand('${ViewItems#Request}')
			responseContent = context.expand('${ViewItems#Response}')
			log.info responseContent

			if (responseContent!="")
			{
				def reqFile2 = new File(sJSONPath+"\\Request_"+sTCName+"\\Request_"+testRunner.testCase.getTestStepAt(2).getName()+".json")
				reqFile2.write(requestContent)
				def respFile2 = new File(sJSONPath+"\\Response_"+sTCName+"\\Response_"+testRunner.testCase.getTestStepAt(2).getName()+".json")
				respFile2.write(responseContent)
				jsonResponse = new JsonSlurper().parseText(responseContent)


				iItemListSize = jsonResponse["PWItemItems"].size()
				log.info "Total Items After Max Length Add Attempt : "+iItemListSize.toString()
				fnUpdateLog("Total Items After Max Length Add Attempt : "+iItemListSize.toString())	

				int iDupItemCount = 0 

				jsonResponse["PWItemItems"].find
				{ 
					//log.info it
					//if(it.PWItemID == 367 && it.Name == "TestItem_367" )  //Just for testing
					//if(it.PWItemID == iNewItemId && it.Name == sNewItemName )  //No need to validate on Item Id, It's auto generated by DB
					if(it.Name == sNewItemName )
					{	
						iDupItemCount = iDupItemCount +1
						bFlag = false
						//log.info "Actual Item Id : "+it.PWItemID.toString() +" matches with Expected Item Id : "+iNewItemId.toString()+" It should NOT be added."
						//fnUpdateLog("Actual Item Id : "+it.PWItemID.toString() +" matches with Expected Item Id : "+iNewItemId.toString()+" It should NOT be added.")
						if(iDupItemCount>=2)
						{
							fnGenerateTCStepsReport("Verify Item Name in View Item Response", "Item should NOT be added ", "Actual Item Name : "+it.Name.toString() +" matches with Expected Item Name : "+sNewItemName+". It should NOT be added.", "Fail")
							log.info "Actual Item Name : "+it.Name.toString() +" matches with Expected Item Name : "+sNewItemName+" It should NOT be added."
							fnUpdateLog("Actual Item Name : "+it.Name.toString() +" matches with Expected Item Name : "+sNewItemName+" It should NOT be added.")
						}
						//return true  //Look for two duplocate Items
					}	
				}

				log.info "iDupItemCount>>>>>>>>>>>>>>>"+iDupItemCount
				if(bFlag == false && iDupItemCount>=2)
				{
					fnGenerateTCStepsReport("Verify Max Length Item Name in View Item Response", "Max Length Item should NOT be added ", "Max Length Item found.", "Fail")
					log.info "Max Length Item added and found. Test failed.."
					fnUpdateLog("Max Length Item added and found. Test failed..")
					testRunner.fail("Max Length Item added and found. Test failed..")
				}
				else
				{
					fnGenerateTCStepsReport("Verify Max Length Item Name in View Item Response", "Max Length Item should NOT be added ", "Max Length Item NOT found successfully", "Pass")
					log.info "Max Length Item NOT found successfully"
					fnUpdateLog("Max Length Item NOT found successfully")
				}
			}
			else
			{
				fnGenerateTCStepsReport("Verify View Item Response", "View Item response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again.", "Fail")
				fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again..\n");
				testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again..\n")
			}
		}
		else
		{
			fnGenerateTCStepsReport("Verify Add Item Response", "Add Item response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length Item is added. Please check response and try again.", "Fail")
			fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length Item is added. Please check response and try again..\n");
			testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length Item is added. Please check response and try again..\n")
		}
	}
	catch (Exception e)
	{
		fnGenerateTCStepsReport("Error Handler ", "There should NOT be any error/exception in the test", "Exception found in current test.", "Fail")
		testRunner.fail("[Max Length Items] failed with exception : "+e)
		fnUpdateLog("[Max Length Items] failed with exception : "+e)
	}
	finally
	{
		log.info "[Max Length Item] Execution Completed Successfully......."
		fnUpdateLog("[Max Length Item] Execution Completed Successfully.......\n")
	}
