					log.info "Max Length Group NOT found successfully"
					fnUpdateLog("Max Length Group NOT found successfully")
				}
			}
			else
			{
				fnGenerateTCStepsReport("Verify View Group Response", "View Group response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again.", "Fail")
				fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again..\n");
				testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again..\n")
			}
		}
		else
		{
			fnGenerateTCStepsReport("Verify Add Group Response", "Add Group response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length Group is added. Please check response and try again.", "Fail")
			fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length Group is added. Please check response and try again..\n");
			testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length Group is added. Please check response and try again..\n")
		}
	}
	catch (Exception e)
	{
		fnGenerateTCStepsReport("Error Handler ", "There should NOT be any error/exception in the test", "Exception found in current test.", "Fail")
		testRunner.fail("[Max Length Groups] failed with exception : "+e)
		fnUpdateLog("[Max Length Groups] failed with exception : "+e)
	}
	finally
	{
		log.info "[Max Length Group] Execution Completed Successfully......."
		fnUpdateLog("[Max Length Group] Execution Completed Successfully.......\n")
	}
