			testRunner.testCase.testSteps[sReqName].disabled = true
		}
		catch (Exception e)
		{
			testRunner.fail("Function [fnRunRequest] failed with exception : "+e)
			testRunner.fail("Function [fnRunRequest] failed with exception : "+e)
		}
	}

