import groovy.json.JsonSlurper

	scripts = testRunner.testCase.testSuite.project.testSuites["Function_Library_TS"];
	scripts.testCases["Reporter_TC"].testSteps["MainReporter"].run(testRunner, context);

	log.info "[Max Length User] Execution Started.........." 
	fnUpdateLog("[Max Length User] Execution Started.........." )
	fnGenerateTCStepsReport("Verify Max Length User", "", "", "")

	def working_Directory = context.expand( '${#Project#Working_Directory}' )
	def (value1, value2) = testRunner.testCase.testSuite.getName().tokenize( '_' )
	String sTSName = value1;
	def ResultPath = context.expand( '${#Project#ResultPath}' )
	//String sJSONPath = working_Directory+"\\InputOutput_JSONs\\"+sTSName
	String sJSONPath = ResultPath+"\\InputOutput_JSONs\\"+sTSName
	String sTCName = testRunner.testCase.getName();
	def iMaxUserId = 0
	def sLatestUserName = ""
	def sNewUserName =""

	try
	{
		fnRunRequest("ViewUsers");

		/////////////////////////////////////////////////////////////////////////////////////////////////////////////////

		def requestContent = context.expand('${ViewUsers#Request}')
		def responseContent = context.expand('${ViewUsers#Response}')
		log.info "request>>>>>"+requestContent
		log.info "response>>>>"+responseContent

		//jsonRequest = new JsonSlurper().parseText(requestContent)

		///////////////////Start - Parse JSON and get Max UserId and set at test case level User NewUserId,NewUserName///////////////////


		if (responseContent!="")
		{
			jsonResponse = new JsonSlurper().parseText(responseContent)

			int iUserListSize = jsonResponse["UserItems"].size()
			log.info "Total Users Before Max Length Add Attempt : "+iUserListSize.toString()
			//logFile.append(new Date().format('yyyyMMddHHmmss')+ " : "+"Total Users Before Max Length Add Attempt : "+iUserListSize.toString()  +".\n")
			fnUpdateLog("Total Users Before Max Length Add Attempt : "+iUserListSize.toString() )

			jsonResponse["UserItems"].each
			{ 
				//log.info it
				if(it.UserId >iMaxUserId)
				{
					iMaxUserId = it.UserId
					sLatestUserName = it.Username
				}
				//log.info "UserID>>>>>>>>>>>>>>>>>>>>>"+ it.UserID 
				//log.info "UserName>>>>>>>>>>>>>>>>>>>"+ it.UserName	
			}
			log.info "Max UserID>>>>>>>>>>>>>>>>>>>>>"+ iMaxUserId
			//logFile.append(new Date().format('yyyyMMddHHmmss')+ " : "+"Max UserID>>>>>>>>>>>>>>>>>>>>>"+ iMaxUserId  +".\n")
			fnUpdateLog("Max UserID>>>>>>>>>>>>>>>>>>>>>"+ iMaxUserId )
			log.info "Latest User Name>>>>>>>>>>>>>>>>>>>>>"+ sLatestUserName  
			//logFile.append(new Date().format('yyyyMMddHHmmss')+ " : "+"Latest User Name>>>>>>>>>>>>>>>>>>>>>"+ sLatestUserName    +".\n")
			fnUpdateLog("Latest User Name>>>>>>>>>>>>>>>>>>>>>"+ sLatestUserName  )

			int iRandomNum = fnRandomNum(10001,99999);
			sNewUserName = "TestUserTestUserTestUserTestUserTestUserTestUserTestUserTestUserTestUserTestUserTestUserTestUser_"+iRandomNum.toString();
			testRunner.testCase.setPropertyValue("NewUserName",sNewUserName)
		}
		else
		{
			log.info "Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please continue adding User..\n";
			fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please continue adding User..\n");
		}


		//Start - Adding a new User
		fnRunRequest("AddUser");

		requestContent = context.expand('${AddUser#Request}')
		log.info requestContent
		responseContent = context.expand('${AddUser#Response}')
		log.info responseContent

		if (responseContent!="")
		{
			def reqF1 = new File(sJSONPath+"\\Request_"+sTCName)
			def resF1 = new File(sJSONPath+"\\Response_"+sTCName)
			if (!reqF1.exists()){reqF1.mkdirs()}
			if (!resF1.exists()){resF1.mkdirs()}

			def reqFile1 = new File(sJSONPath+"\\Request_"+sTCName+"\\Request_"+testRunner.testCase.getTestStepAt(1).getName()+".json")
