				log.info "Max Length User : "+sNewUserName+" should NOT be added"
				fnUpdateLog("Max Length User : "+sNewUserName+" should NOT be added")	
				testRunner.fail("Max Length User : "+sNewUserName+" should NOT be added")
			}
			//End - Adding a new User

