					if(it.Username == sNewUserName )
					{	
						iDupUserCount = iDupUserCount +1
						bFlag = false
						//log.info "Actual User Id : "+it.UserID.toString() +" matches with Expected User Id : "+iNewUserId.toString()+" It should NOT be added."
						//fnUpdateLog("Actual User Id : "+it.UserID.toString() +" matches with Expected User Id : "+iNewUserId.toString()+" It should NOT be added.")
						if(iDupUserCount>=2)
						{
							fnGenerateTCStepsReport("Verify User Name in View User Response", "User should NOT be added ", "Actual User Name : "+it.Username.toString() +" matches with Expected User Name : "+sNewUserName+". It should NOT be added.", "Fail")
							log.info "Actual User Name : "+it.Username.toString() +" matches with Expected User Name : "+sNewUserName+" It should NOT be added."
							fnUpdateLog("Actual User Name : "+it.Username.toString() +" matches with Expected User Name : "+sNewUserName+" It should NOT be added.")
						}
						//return true  //Look for two duplocate Users
					}	
				}

				log.info "iDupUserCount>>>>>>>>>>>>>>>"+iDupUserCount
				if(bFlag == false && iDupUserCount>=2)
				{
					fnGenerateTCStepsReport("Verify Max Length User Name in View User Response", "Max Length User should NOT be added ", "Max Length User found.", "Fail")
					log.info "Max Length User added and found. Test failed.."
					fnUpdateLog("Max Length User added and found. Test failed..")
					testRunner.fail("Max Length User added and found. Test failed..")
				}
				else
				{
					fnGenerateTCStepsReport("Verify Max Length User Name in View User Response", "Max Length User should NOT be added ", "Max Length User NOT found successfully", "Pass")
					log.info "Max Length User NOT found successfully"
					fnUpdateLog("Max Length User NOT found successfully")
				}
			}
			else
			{
				fnGenerateTCStepsReport("Verify View User Response", "View User response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again.", "Fail")
				fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again..\n");
				testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again..\n")
			}
		}
		else
		{
			fnGenerateTCStepsReport("Verify Add User Response", "Add User response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length User is added. Please check response and try again.", "Fail")
			fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length User is added. Please check response and try again..\n");
			testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length User is added. Please check response and try again..\n")
		}
	}
	catch (Exception e)
	{
		fnGenerateTCStepsReport("Error Handler ", "There should NOT be any error/exception in the test", "Exception found in current test.", "Fail")
		testRunner.fail("[Max Length Users] failed with exception : "+e)
		fnUpdateLog("[Max Length Users] failed with exception : "+e)
	}
	finally
	{
		log.info "[Max Length User] Execution Completed Successfully......."
		fnUpdateLog("[Max Length User] Execution Completed Successfully.......\n")
	}
