						log.info "it.Description.toString()>>>>"+it.Description.toString()
						log.info "it.HourlyLaborRate.toString()>>>>"+it.HourlyLaborRate.toString()
						if (it.DisplayTenantResponsibility.toString() == sDisplayTenantResponsibility && it.DisplayServiceType.toString() == sDisplayServiceType  && it.Description.toString() == sDescription && it.HourlyLaborRate.toString() == sHourlyLaborRate)
