							log.info "Actual Flow Name : "+it.FlowName.toString() +" matches with Expected Flow Name : "+sNewFlowName+" It should NOT be added."
							fnUpdateLog("Actual Flow Name : "+it.FlowName.toString() +" matches with Expected Flow Name : "+sNewFlowName+" It should NOT be added.")
						}
						//return true  //Look for two duplocate Flows
					}	
				}

				log.info "iDupFlowCount>>>>>>>>>>>>>>>"+iDupFlowCount
				if(bFlag == false && iDupFlowCount>=2)
				{
					fnGenerateTCStepsReport("Verify Duplicate Flow Name in View Flow Response", "Duplicate Flow should NOT be added ", "Two duplicate Flows found.", "Fail")
					log.info "Duplicate Flow added and found. Test failed.."
					fnUpdateLog("Duplicate Flow added and found. Test failed..")
					testRunner.fail("Duplicate Flow added and found. Test failed..")
				}
				else
				{
					fnGenerateTCStepsReport("Verify Duplicate Flow Name in View Flow Response", "Duplicate Flow should NOT be added ", "Duplicate Flow NOT found successfully", "Pass")
