import groovy.json.JsonSlurper

	scripts = testRunner.testCase.testSuite.project.testSuites["Function_Library_TS"];
	scripts.testCases["Reporter_TC"].testSteps["MainReporter"].run(testRunner, context);

	log.info "[Max Length Flow] Execution Started.........." 
	fnUpdateLog("[Max Length Flow] Execution Started.........." )
	fnGenerateTCStepsReport("Verify Max Length Flow", "", "", "")

	def working_Directory = context.expand( '${#Project#Working_Directory}' )
	def (value1, value2) = testRunner.testCase.testSuite.getName().tokenize( '_' )
	String sTSName = value1;
	def ResultPath = context.expand( '${#Project#ResultPath}' )
	//String sJSONPath = working_Directory+"\\InputOutput_JSONs\\"+sTSName
	String sJSONPath = ResultPath+"\\InputOutput_JSONs\\"+sTSName
	String sTCName = testRunner.testCase.getName();
	def iMaxFlowId = 0
	def sLatestFlowName = ""
	def sNewFlowName =""

	try
	{
		fnRunRequest("ViewFlows");

		/////////////////////////////////////////////////////////////////////////////////////////////////////////////////

		def requestContent = context.expand('${ViewFlows#Request}')
		def responseContent = context.expand('${ViewFlows#Response}')
		log.info "request>>>>>"+requestContent
		log.info "response>>>>"+responseContent

		//jsonRequest = new JsonSlurper().parseText(requestContent)

		///////////////////Start - Parse JSON and get Max FlowId and set at test case level Flow NewFlowId,NewFlowName///////////////////


		if (responseContent!="")
		{
			jsonResponse = new JsonSlurper().parseText(responseContent)

			int iFlowListSize = jsonResponse["PWFlowItems"].size()
			log.info "Total Flows Before Max Length Add Attempt : "+iFlowListSize.toString()
			//logFile.append(new Date().format('yyyyMMddHHmmss')+ " : "+"Total Flows Before Max Length Add Attempt : "+iFlowListSize.toString()  +".\n")
			fnUpdateLog("Total Flows Before Max Length Add Attempt : "+iFlowListSize.toString() )

			jsonResponse["PWFlowItems"].each
			{ 
				//log.info it
				if(it.PWFlowID.toInteger() >iMaxFlowId.toInteger())
				{
					iMaxFlowId = it.PWFlowID.toInteger()
					sLatestFlowName = it.Name
				}
				//log.info "FlowID>>>>>>>>>>>>>>>>>>>>>"+ it.FlowID 
				//log.info "FlowName>>>>>>>>>>>>>>>>>>>"+ it.FlowName	
			}
			log.info "Max FlowID>>>>>>>>>>>>>>>>>>>>>"+ iMaxFlowId
			//logFile.append(new Date().format('yyyyMMddHHmmss')+ " : "+"Max FlowID>>>>>>>>>>>>>>>>>>>>>"+ iMaxFlowId  +".\n")
			fnUpdateLog("Max FlowID>>>>>>>>>>>>>>>>>>>>>"+ iMaxFlowId )
			log.info "Latest Flow Name>>>>>>>>>>>>>>>>>>>>>"+ sLatestFlowName  
			//logFile.append(new Date().format('yyyyMMddHHmmss')+ " : "+"Latest Flow Name>>>>>>>>>>>>>>>>>>>>>"+ sLatestFlowName    +".\n")
			fnUpdateLog("Latest Flow Name>>>>>>>>>>>>>>>>>>>>>"+ sLatestFlowName  )

			int iRandomNum = fnRandomNum(10001,99999);
			sNewFlowName = "TestFlowTestFlowTestFlowTestFlowTestFlowTestFlowTestFlowTestFlowTestTestFlowTestFlowTestFlowTestFlowTestFlowTestFlowTestFlowTestFlowTestTestFlowTestFlowTestFlowTestFlowTestFlowTestFlowTestFlowTestFlowTestTestFlowTestFlowTestFlowTestFlowTestFlowTestFlow_"+iRandomNum.toString();
			testRunner.testCase.setPropertyValue("NewFlowName",sNewFlowName)
		}
		else
		{
			log.info "Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please continue adding Flow..\n";
			fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please continue adding Flow..\n");
		}


		//Start - Adding a new Flow
		fnRunRequest("AddFlow");

		requestContent = context.expand('${AddFlow#Request}')
		log.info requestContent
		responseContent = context.expand('${AddFlow#Response}')
		log.info responseContent

		if (responseContent!="")
		{
			def reqF1 = new File(sJSONPath+"\\Request_"+sTCName)
			def resF1 = new File(sJSONPath+"\\Response_"+sTCName)
			if (!reqF1.exists()){reqF1.mkdirs()}
			if (!resF1.exists()){resF1.mkdirs()}

			def reqFile1 = new File(sJSONPath+"\\Request_"+sTCName+"\\Request_"+testRunner.testCase.getTestStepAt(1).getName()+".json")
			reqFile1.write(requestContent)
