			//if(sKey=="Status" && sValue=="Add Flow Failed")
			if(sCode=="RQ500" && sStatus=="500")
			{
				fnGenerateTCStepsReport("Verify Max Length Flow Response", "Max Length Flow response should throw errors", "Max Length Flow NOT added and response has errors", "Pass")
				log.info "Max Length Flow : "+sNewFlowName+" NOT added successfully"
				fnUpdateLog("Max Length Flow : "+sNewFlowName+" NOT added successfully")	
			}
			else
			{
				fnGenerateTCStepsReport("Verify Max Length Flow Response", "Max Length Flow response should throw errors", "Max Length Flow added and response has NO errors", "Fail")
				log.info "Max Length Flow : "+sNewFlowName+" should NOT be added"
				fnUpdateLog("Max Length Flow : "+sNewFlowName+" should NOT be added")	
				testRunner.fail("Max Length Flow : "+sNewFlowName+" should NOT be added")
			}
			//End - Adding a new Flow

			//Start - Verify newly added Flow
			boolean bFlag = true
			fnRunRequest("ViewFlows");
			requestContent = context.expand('${ViewFlows#Request}')
			responseContent = context.expand('${ViewFlows#Response}')
			log.info responseContent

			if (responseContent!="")
			{
				def reqFile2 = new File(sJSONPath+"\\Request_"+sTCName+"\\Request_"+testRunner.testCase.getTestStepAt(2).getName()+".json")
				reqFile2.write(requestContent)
				def respFile2 = new File(sJSONPath+"\\Response_"+sTCName+"\\Response_"+testRunner.testCase.getTestStepAt(2).getName()+".json")
				respFile2.write(responseContent)
				jsonResponse = new JsonSlurper().parseText(responseContent)


				iFlowListSize = jsonResponse["PWFlowItems"].size()
				log.info "Total Flows After Max Length Add Attempt : "+iFlowListSize.toString()
				fnUpdateLog("Total Flows After Max Length Add Attempt : "+iFlowListSize.toString())	

				int iDupFlowCount = 0 

				jsonResponse["PWFlowItems"].find
				{ 
					//log.info it
					//if(it.FlowID == 367 && it.FlowName == "TestFlow_367" )  //Just for testing
					//if(it.FlowID == iNewFlowId && it.FlowName == sNewFlowName )  //No need to validate on Flow Id, It's auto generated by DB
					if(it.Name == sNewFlowName )
					{	
						iDupFlowCount = iDupFlowCount +1
						bFlag = false
						//log.info "Actual Flow Id : "+it.FlowID.toString() +" matches with Expected Flow Id : "+iNewFlowId.toString()+" It should NOT be added."
						//fnUpdateLog("Actual Flow Id : "+it.FlowID.toString() +" matches with Expected Flow Id : "+iNewFlowId.toString()+" It should NOT be added.")
						if(iDupFlowCount>=2)
						{
							fnGenerateTCStepsReport("Verify Flow Name in View Flow Response", "Flow should NOT be added ", "Actual Flow Name : "+it.Name.toString() +" matches with Expected Flow Name : "+sNewFlowName+". It should NOT be added.", "Fail")
							log.info "Actual Flow Name : "+it.Name.toString() +" matches with Expected Flow Name : "+sNewFlowName+" It should NOT be added."
							fnUpdateLog("Actual Flow Name : "+it.Name.toString() +" matches with Expected Flow Name : "+sNewFlowName+" It should NOT be added.")
						}
						//return true  //Look for two duplocate Flows
					}	
				}

				log.info "iDupFlowCount>>>>>>>>>>>>>>>"+iDupFlowCount
				if(bFlag == false && iDupFlowCount>=2)
				{
					fnGenerateTCStepsReport("Verify Max Length Flow Name in View Flow Response", "Max Length Flow should NOT be added ", "Max Length Flow found.", "Fail")
					log.info "Max Length Flow added and found. Test failed.."
					fnUpdateLog("Max Length Flow added and found. Test failed..")
					testRunner.fail("Max Length Flow added and found. Test failed..")
				}
				else
				{
					fnGenerateTCStepsReport("Verify Max Length Flow Name in View Flow Response", "Max Length Flow should NOT be added ", "Max Length Flow NOT found successfully", "Pass")
					log.info "Max Length Flow NOT found successfully"
					fnUpdateLog("Max Length Flow NOT found successfully")
				}
			}
			else
			{
				fnGenerateTCStepsReport("Verify View Flow Response", "View Flow response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again.", "Fail")
				fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again..\n");
				testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(2).getName()+ "] request. Please check response and try again..\n")
			}
		}
		else
		{
			fnGenerateTCStepsReport("Verify Add Flow Response", "Add Flow response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length Flow is added. Please check response and try again.", "Fail")
			fnUpdateLog("Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length Flow is added. Please check response and try again..\n");
			testRunner.fail("Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request or Max Length Flow is added. Please check response and try again..\n")
		}
	}
	catch (Exception e)
	{
		fnGenerateTCStepsReport("Error Handler ", "There should NOT be any error/exception in the test", "Exception found in current test.", "Fail")
		testRunner.fail("[Max Length Flows] failed with exception : "+e)
		fnUpdateLog("[Max Length Flows] failed with exception : "+e)
	}
	finally
	{
		log.info "[Max Length Flow] Execution Completed Successfully......."
		fnUpdateLog("[Max Length Flow] Execution Completed Successfully.......\n")
