import groovy.json.JsonSlurper

	scripts = testRunner.testCase.testSuite.project.testSuites["Function_Library_TS"];
	scripts.testCases["Reporter_TC"].testSteps["MainReporter"].run(testRunner, context);

	log.info "[Get Photo Link Token] Execution Started.........." 
	fnUpdateLog("[Get Photo Link Token] Execution Started..........")
	fnGenerateTCStepsReport("Verify PhotoLink Token", "", "", "")

	def working_Directory = context.expand( '${#Project#Working_Directory}' )
	def (value1, value2) = testRunner.testCase.testSuite.getName().tokenize( '_' )
	String sTSName = value1;
	def ResultPath = context.expand( '${#Project#ResultPath}' )
	//String sJSONPath = working_Directory+"\\InputOutput_JSONs\\"+sTSName
	String sJSONPath = ResultPath+"\\InputOutput_JSONs\\"+sTSName
	String sTCName = testRunner.testCase.getName();


	try
	{
		fnRunRequest("GetTokenRequest");

		/////////////////////////////////////////////////////////////////////////////////////////////////////////////////
		//def sCount = testRunner.testCase.testSuite.getPropertyValue("TestCount")
		def requestContent = context.expand('${GetTokenRequest#Request}')
		def responseContent = context.expand('${GetTokenRequest#Response}')
		log.info "request>>>>>"+requestContent
		log.info "response>>>>"+responseContent
		String sToken = ""
		//jsonRequest = new JsonSlurper().parseText(requestContent)
		if (responseContent!="")
		{

			///////////////////Start - Parse JSON and get Max FlowId and set at test case level Flow NewFlowId,NewFlowName///////////////////
			jsonResponse = new JsonSlurper().parseText(responseContent)

			jsonResponse = new JsonSlurper().parseText(responseContent)
			sToken = jsonResponse.token
			log.info sToken
			//if(sKey=="Status" && sValue=="Add Corporate Failed")
			if(sToken!="")
			{
				testRunner.testCase.testSuite.setPropertyValue("Token",sToken)
				fnGenerateTCStepsReport("Verify Token for Photo Link", "Photo Link Token should be retrieved", "Photo Link Token ["+sToken+"] retrieved successfully", "Pass")
				log.info "Photo Link Token ["+sToken+"] retrieved successfully"
				fnUpdateLog("Photo Link Token ["+sToken+"] retrieved successfully")	
			}
			else
			{
				fnGenerateTCStepsReport("Verify Token for Photo Link", "Photo Link Token should be retrieved", "Photo Link Token NOT retrieved. Please check Property Id or service end-point", "Fail")
				log.info "Photo Link Token NOT retrieved. Please check Property Id or service end-point"
				fnUpdateLog("Photo Link Token NOT retrieved. Please check Property Id or service end-point")	
			}
		}
		else
		{
			fnGenerateTCStepsReport("Verify Photo Link Token Response", "Photo Link Token response should be retrieved", "Response is Null for ["+ testRunner.testCase.getTestStepAt(1).getName()+ "] request. Please try again with valid data and service end-point..", "Fail")
			fnUpdateLog("Response is Null for ["+ sTCName+ "] request. Please try again with valid data and service end-point..");
			testRunner.fail("Response is Null for ["+ sTCName+ "] request. Please try again with valid data and service end-point..")
		}
	}
	catch (Exception e)
	{
		fnGenerateTCStepsReport("Error Handler ", "There should NOT be any error/exception in the test", "Exception found in current test.", "Fail")
		testRunner.fail("[Get Photo Link Token] failed with exception : "+e)
		fnUpdateLog("[Get Photo Link Token] failed with exception : "+e)
	}
	finally
	{
		log.info "[Get Photo Link Token] Execution Completed Successfully......."
		fnUpdateLog("[Get Photo Link Token] Execution Completed Successfully.......\n")
	}
