			sAddress = jsonResponse.Address
			log.info sAddress
			//if(sKey=="Status" && sValue=="Add Corporate Failed")
			if(sAddress!="")
			{
				testRunner.testCase.testSuite.setPropertyValue("Address",sAddress)
				fnGenerateTCStepsReport("Verify Info for Photo Link Token", "Info for Photo Link Token should be retrieved", "Info for Photo Link Token [Address : "+sAddress+"] retrieved successfully", "Pass")
				log.info "Info for Photo Link Token [Address : "+sAddress+"] retrieved successfully"
				fnUpdateLog("Info for Photo Link Token [Address : "+sAddress+"] retrieved successfully")	
			}
			else
			{
				fnGenerateTCStepsReport("Verify Info for Photo Link Token", "Info for Photo Link Token should be retrieved", "Info for Photo Link Token NOT retrieved. Please check for valid token or service end-point", "Fail")
				log.info "Info for Photo Link Token NOT retrieved. Please check for valid token or service end-point"
				fnUpdateLog("Info for Photo Link Token NOT retrieved. Please check for valid token or service end-point")	
			}
		}
		else
