			testRunner.fail("Response is Null for ["+ sTCName+ "] request. Please try again with valid data and service end-point..")
		}
	}
	catch (Exception e)
	{
		fnGenerateTCStepsReport("Error Handler ", "There should NOT be any error/exception in the test", "Exception found in current test.", "Fail")
		testRunner.fail("[Get Photo Link Info] failed with exception : "+e)
		fnUpdateLog("[Get Photo Link Info] failed with exception : "+e)
	}
	finally
	{
		log.info "[Get Photo Link Info] Execution Completed Successfully......."
