			//if(sKey=="Status" && sValue=="Add Corporate Failed")
			if(sAddress!="")
			{
				testRunner.testCase.testSuite.setPropertyValue("Address",sAddress)
				fnGenerateTCStepsReport("Verify Info for Invalid Photo Link Token", "Info for Photo Link Token should be retrieved", "Info for Photo Link Token [Address : "+sAddress+"] retrieved successfully", "Pass")
				log.info "Info for Invalid Photo Link Token [Address : "+sAddress+"] retrieved successfully"
