			}
			else
			{
				fnGenerateTCStepsReport("Verify Info for Invalid Photo Link Token", "Info for Photo Link Token should be retrieved", "Info for Photo Link Token NOT retrieved. Please check for valid token or service end-point", "Fail")
				log.info "Info for Invalid Photo Link Token NOT retrieved. Please check for valid token or service end-point"
				fnUpdateLog("Info for Invalid Photo Link Token NOT retrieved. Please check for valid token or service end-point")	
			}
		}
