				if(sExpResult == "" && sActualResult == "")   //Main step
				{
					sStepNo = testRunner.testCase.getPropertyValue("StepCounter")
					iCurrentStepNo = sStepNo.toInteger()+1
					testRunner.testCase.setPropertyValue("StepCounter",iCurrentStepNo.toString())

